mapper.select:
SELECT NAME
FROM PRODUCT_CATEGORY
WHERE NAME = #{third}
AND DEPTH = 2
AND PARENT_ID = (
    SELECT ID 
    FROM PRODUCT_CATEGORY 
    WHERE NAME = #{second} 
    AND DEPTH = 1
    AND PARENT_ID = (
        SELECT ID
        FROM PRODUCT_CATEGORY
        WHERE NAME = #{first}
        AND DEPTH = 0
    )
)

SELECT PRO_CODE, PRO_NAME, PRO_PETS, PRO_TYPE, PRO_CATEGORY, PRO_ONOFF, PRO_DATE FROM PRODUCT WHERE PRO_ONOFF = #{proOnOff}
AND PRO_PETS = #{petType}
AND PRO_TYPE = #{proType}
AND PRO_CATEGORY = #{detailType}
ORDER BY PRO_DATE DESC
